export {default as hoare} from './partition/hoare.js';
export {default as lomuto} from './partition/lomuto.js';
export {default as yaroslavskiy} from './partition/yaroslavskiy.js';
export {default as firstInversion} from './utils/firstInversion.js';
export {default as isPartitioned} from './utils/isPartitioned.js';
export {default as whole} from './utils/whole.js';

import assert from 'assert';

/**
 * @param {Function} compare
 * @param {Array} a
 * @param {number} i
 * @param {number} j
 * @return {number}
 */
const hoare = (compare, a, i, j) => {
	assert(i < j);

	const o = i;
	const x = a[o];

	while (true) {
		while (true) {
			--j;

			if (i >= j) {
				a[o] = a[j];
				a[j] = x;
				return j;
			}

			if (compare(x, a[j]) > 0) {
				break;
			}
		}

		while (true) {
			++i;

			if (i >= j) {
				a[o] = a[j];
				a[j] = x;
				return j;
			}

			if (compare(x, a[i]) < 0) {
				break;
			}
		}

		// Invariant i < j

		const t = a[i];
		a[i] = a[j];
		a[j] = t;
	}
};

export default hoare;

import assert from 'assert';

/**
 * @param {Function} compare
 * @param {Array} a
 * @param {number} i
 * @param {number} j
 * @return {number}
 */
const lomuto = (compare, a, i, j) => {
	assert(i < j);

	const p = a[i];
	let k = i + 1;

	--j;

	while (k <= j) {
		if (compare(p, a[k]) <= 0) {
			const t = a[k];
			a[k] = a[j];
			a[j] = t;

			--j;
		} else {
			++k;
		}
	}

	a[i] = a[k - 1];
	a[k - 1] = p;

	return k - 1;
};

export default lomuto;

import assert from 'assert';

/**
 * See http://cs.stackexchange.com/a/24099/20711
 *
 * @param {Function} compare
 * @param {Array} a
 * @param {number} i
 * @param {number} j
 * @return {[number, number]}
 */
const yaroslavskiy = (compare, a, i, j) => {
	assert(i < j);
	--j;

	// Choose outermost elements as pivots
	if (compare(a[i], a[j]) > 0) {
		const t = a[i];
		a[i] = a[j];
		a[j] = t;
	}

	const p = a[i];
	const q = a[j];

	// Partition a according to invariant below
	let l = i + 1;
	let g = j - 1;
	let k = l;

	while (k <= g) {
		if (compare(p, a[k]) > 0) {
			const t = a[k];
			a[k] = a[l];
			a[l] = t;

			++l;
		} else if (compare(q, a[k]) <= 0) {
			while (compare(a[g], q) > 0 && k < g) {
				--g;
			}

			const t = a[k];
			a[k] = a[g];
			a[g] = t;
			--g;

			if (compare(p, a[k]) > 0) {
				const t = a[k];
				a[k] = a[l];
				a[l] = t;
				++l;
			}
		}

		++k;
	}

	--l;
	++g;

	// Swap pivots to final place

	const t1 = a[i];
	a[i] = a[l];
	a[l] = t1;

	const t2 = a[j];
	a[j] = a[g];
	a[g] = t2;

	return [l, g];
};

export default yaroslavskiy;

/**
 * Checks whether range [left,right[ of array is partitioned according to pivot
 * p. Returns the index of the first inversion.
 *
 * @param {Function} compare
 * @param {ArrayLike} array
 * @param {number} left
 * @param {number} right
 * @param {number[]} pivots
 * @param {number} pi
 * @param {number} pj
 * @return {number}
 */
const firstInversion = (compare, array, left, right, pivots, pi, pj) => {
	let p = pivots[pi];
	let x = array[p];
	for (let k = left; k < p; ++k) {
		if (compare(array[k], x) > 0) return k;
	}

	while (++pi < pj) {
		const q = pivots[pi];
		const y = array[q];
		for (let k = p + 1; k < q; ++k) {
			if (compare(array[k], x) < 0) return k;
			if (compare(array[k], y) > 0) return k;
		}

		p = q;
		x = y;
	}

	for (let k = p + 1; k < right; ++k) {
		if (compare(array[k], x) < 0) return k;
	}

	return right;
};

export default firstInversion;

const whole = (partition) => (compare, array) =>
	partition(compare, array, 0, array.length);

export default whole;

